import 'package:flutter/material.dart';import 'package:flutter/services.dart';import 'home_page.dart';void main() => runApp(MyApp());const Color kOrange = const Color(0xFFF9AA33); // Reply orange class MyAppclass MyApp extends StatelessWidget {  @override  Widget build(BuildContext context) {    return MaterialApp(      home: HomePage(),    );  }}class UpperCaseTextFormatter extends TextInputFormatter {  @override  TextEditingValue formatEditUpdate(      TextEditingValue oldValue, TextEditingValue newValue) {    return TextEditingValue(      text: newValue.text.toUpperCase(),      selection: newValue.selection,    );  }}